import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import './UserPage.css';
import axios from 'axios';
import { toast } from 'react-toastify';

function UserPage() {
  const { username } = useParams();
  const [message, setMessage] = useState('');
  const [senderName, setSenderName] = useState('');
  const [isAnonymous, setIsAnonymous] = useState(false);
  const [messages, setMessages] = useState([]);
  const [messageCount, setMessageCount] = useState(0);
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('user'));
  const isOwner = user?.username === username;
  const [ownerDisplayName, setOwnerDisplayName] = useState('');
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);
  const [editDisplayName, setEditDisplayName] = useState('');
  const [currentPassword, setCurrentPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmNewPassword, setConfirmNewPassword] = useState('');
  const [deletePassword, setDeletePassword] = useState('');
  const [isDeleteModalOpen, setIsDeleteModalOpen] = useState(false);
  const [previousMessageCount, setPreviousMessageCount] = useState(0);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await axios.get(`/api/users/${username}`);
        console.log('ÏÑúÎ≤ÑÏóêÏÑú Î∞õÏùÄ ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥:', response.data);

        if (response.data.displayName) {
          setOwnerDisplayName(response.data.displayName);

          if (isOwner) {
            const userData = JSON.parse(localStorage.getItem('user'));
            const updatedUserData = {
              ...userData,
              displayName: response.data.displayName
            };
            localStorage.setItem('user', JSON.stringify(updatedUserData));
          }
        }
      } catch (error) {
        console.error('ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Ï°∞Ìöå Ïã§Ìå®:', error);
      }
    };

    const userData = JSON.parse(localStorage.getItem('user'));
    if (userData?.displayName) {
      setOwnerDisplayName(userData.displayName);
    }

    fetchUserData();
  }, [username, isOwner]);

  useEffect(() => {
    const fetchMessages = async () => {
      try {
        const response = await axios.get(`/api/messages/${username}?isOwner=${isOwner}`);
        if (isOwner) {
          if (response.data.isChristmas) {
            setMessages(response.data.messages);
          } else {
            const newCount = response.data.count;
            const savedCount = parseInt(localStorage.getItem(`${username}_messageCount`) || '0');

            if (savedCount === 0) {
              toast.info(`ÌòÑÏû¨ ${newCount}Í∞úÏùò Î©îÏãúÏßÄÍ∞Ä ÏûàÏäµÎãàÎã§! üéÑ`);
            } else if (newCount > savedCount) {
              const newMessages = newCount - savedCount;
              toast.info(`${newMessages}Í∞úÏùò ÏÉàÎ°úÏö¥ Î©îÏãúÏßÄÍ∞Ä ÎèÑÏ∞©ÌñàÏäµÎãàÎã§! üéÑ`);
            }

            setMessageCount(newCount);
            localStorage.setItem(`${username}_messageCount`, newCount.toString());
          }
        } else {
          setMessageCount(response.data.count);
        }
      } catch (error) {
        console.error('Î©îÏãúÏßÄ Ï°∞Ìöå Ïã§Ìå®:', error);
      }
    };

    fetchMessages();

    const interval = setInterval(fetchMessages, 60000);

    return () => clearInterval(interval);
  }, [username, isOwner]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/messages', {
        text: message,
        recipientUsername: username,
        senderName: isAnonymous ? 'ÏùµÎ™Ö' : senderName
      });

      // Î©îÏãúÏßÄ Ï†ÑÏÜ° ÌõÑ Ìèº Ï¥àÍ∏∞Ìôî
      setMessage('');
      setSenderName('');
      setIsAnonymous(false);

      // Î©îÏãúÏßÄ Ïπ¥Ïö¥Ìä∏ ÏóÖÎç∞Ïù¥Ìä∏
      const response = await axios.get(`/api/messages/${username}?isOwner=false`);
      setMessageCount(response.data.count);

      toast.success('Î©îÏãúÏßÄÍ∞Ä Ï†ÑÏÜ°ÎêòÏóàÏäµÎãàÎã§!');
    } catch (error) {
      console.error('Î©îÏãúÏßÄ Ï†ÄÏû• Ïã§Ìå®:', error);
      // ÏÑúÎ≤ÑÏóêÏÑú Î≥¥ÎÇ∏ ÏóêÎü¨ Î©îÏãúÏßÄ ÌëúÏãú
      toast.error(error.response?.data?.error || 'Î©îÏãúÏßÄ Ï†ÄÏû•Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem(`${username}_messageCount`);
    localStorage.removeItem('user');
    navigate('/login');
  };

  const copyPageUrl = () => {
    const url = window.location.href;
    navigator.clipboard.writeText(url);
    toast.success('ÌéòÏù¥ÏßÄ Ï£ºÏÜåÍ∞Ä Î≥µÏÇ¨ÎêòÏóàÏäµÎãàÎã§!');
  };

  // ÌîÑÎ°úÌïÑ ÏàòÏ†ï Î™®Îã¨ Ïó¥Í∏∞
  const openEditModal = () => {
    setEditDisplayName(ownerDisplayName);
    setCurrentPassword('');
    setNewPassword('');
    setConfirmNewPassword('');
    setIsEditModalOpen(true);
  };

  // ÌîÑÎ°úÌïÑ ÏàòÏ†ï Ï≤òÎ¶¨
  const handleEditProfile = async (e) => {
    e.preventDefault();

    // ÌòÑÏû¨ ÎπÑÎ∞ÄÎ≤àÌò∏ ÌïÑÏàò Ï≤¥ÌÅ¨
    if (!currentPassword) {
      toast.error('ÌòÑÏû¨ ÎπÑÎ∞ÄÎ≤àÌò∏Î•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }

    // ÏÉà ÎπÑÎ∞ÄÎ≤àÌò∏ ÌôïÏù∏
    if (newPassword && newPassword !== confirmNewPassword) {
      toast.error('ÏÉà ÎπÑÎ∞ÄÎ≤àÌò∏Í∞Ä ÏùºÏπòÌïòÏßÄ ÏïäÏäµÎãàÎã§.');
      return;
    }

    try {
      const response = await axios.put(`/api/users/${username}`, {
        displayName: editDisplayName,
        currentPassword,
        newPassword: newPassword || undefined
      });

      // ÏÉÅÌÉú ÏóÖÎç∞Ïù¥Ìä∏
      setOwnerDisplayName(response.data.displayName);

      // localStorage ÏóÖÎç∞Ïù¥Ìä∏
      const userData = JSON.parse(localStorage.getItem('user'));
      const updatedUserData = {
        ...userData,
        displayName: response.data.displayName
      };
      localStorage.setItem('user', JSON.stringify(updatedUserData));

      // ÏûÖÎ†• ÌïÑÎìú Ï¥àÍ∏∞Ìôî
      setCurrentPassword('');
      setNewPassword('');
      setConfirmNewPassword('');

      setIsEditModalOpen(false);
      toast.success('ÌîÑÎ°úÌïÑÏù¥ ÏàòÏ†ïÎêòÏóàÏäµÎãàÎã§.');
    } catch (error) {
      console.error('ÌîÑÎ°úÌïÑ ÏàòÏ†ï Ïã§Ìå®:', error);
      toast.error(error.response?.data?.error || 'ÌîÑÎ°úÌïÑ ÏàòÏ†ïÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
    }
  };

  // ÌöåÏõêÌÉàÌá¥ Ï≤òÎ¶¨
  const handleDeleteAccount = async (e) => {
    e.preventDefault();

    if (!deletePassword) {
      toast.error('ÎπÑÎ∞ÄÎ≤àÌò∏Î•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }

    // ÏÇ¨Ïö©Ïûê Ï†ïÏùò confirm ÎåÄÏã† toast ÏÇ¨Ïö©
    toast.warn(
      'Ï†ïÎßêÎ°ú ÌÉàÌá¥ÌïòÏãúÍ≤†ÏäµÎãàÍπå? Ïù¥ ÏûëÏóÖÏùÄ ÎêòÎèåÎ¶¥ Ïàò ÏóÜÏäµÎãàÎã§.',
      {
        autoClose: false,
        closeOnClick: false,
        draggable: false,
        closeButton: false,
        buttons: [
          {
            label: 'Ïòà',
            onClick: async () => {
              try {
                await axios.delete(`/api/users/${username}`, {
                  data: { password: deletePassword }
                });

                localStorage.removeItem('user');
                toast.success('ÌöåÏõêÌÉàÌá¥Í∞Ä ÏôÑÎ£åÎêòÏóàÏäµÎãàÎã§.');
                navigate('/');
              } catch (error) {
                console.error('ÌöåÏõêÌÉàÌá¥ Ïã§Ìå®:', error);
                toast.error(error.response?.data?.error || 'ÌöåÏõêÌÉàÌá¥Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
              }
            }
          },
          {
            label: 'ÏïÑÎãàÏò§',
            onClick: () => toast.dismiss()
          }
        ]
      }
    );
  };

  return (
    <div className="user-page">
      <header className="user-header">
        <h2>{ownerDisplayName || 'ÏÇ¨Ïö©Ïûê'}ÎãòÏùò ÌÅ¨Î¶¨Ïä§ÎßàÏä§ Î©îÏãúÏßÄÌï®</h2>
        {isOwner ? (
          <div className="owner-actions">
            <button onClick={openEditModal} className="edit-btn">ÌîÑÎ°úÌïÑ ÏàòÏ†ï</button>
            <button onClick={copyPageUrl} className="share-btn">ÌéòÏù¥ÏßÄ Í≥µÏú†ÌïòÍ∏∞</button>
            <button onClick={handleLogout} className="logout-btn">Î°úÍ∑∏ÏïÑÏõÉ</button>
            <button 
              onClick={() => setIsDeleteModalOpen(true)} 
              className="delete-btn"
            >
              ÌöåÏõêÌÉàÌá¥
            </button>
          </div>
        ) : (
          <div className="message-count">
            ÌòÑÏû¨ÍπåÏßÄ {messageCount}Í∞úÏùò Î©îÏãúÏßÄÍ∞Ä ÎèÑÏ∞©ÌñàÏäµÎãàÎã§
          </div>
        )}
      </header>

      {/* ÌîÑÎ°úÌïÑ ÏàòÏ†ï Î™®Îã¨ */}
      {isEditModalOpen && (
        <div className="modal-overlay">
          <div className="modal">
            <h3>ÌîÑÎ°úÌïÑ ÏàòÏ†ï</h3>
            <form onSubmit={handleEditProfile} className="edit-form">
              <div className="form-group">
                <label>ÌëúÏãú Ïù¥Î¶Ñ</label>
                <input
                  type="text"
                  value={editDisplayName}
                  onChange={(e) => setEditDisplayName(e.target.value)}
                  placeholder="ÌëúÏãúÎê† Ïù¥Î¶ÑÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
                  required
                />
              </div>
              <div className="form-group">
                <label>ÌòÑÏû¨ ÎπÑÎ∞ÄÎ≤àÌò∏</label>
                <input
                  type="password"
                  value={currentPassword}
                  onChange={(e) => setCurrentPassword(e.target.value)}
                  placeholder="ÌòÑÏû¨ ÎπÑÎ∞ÄÎ≤àÌò∏Î•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
                  required
                />
              </div>
              <div className="form-group">
                <label>ÏÉà ÎπÑÎ∞ÄÎ≤àÌò∏</label>
                <input
                  type="password"
                  value={newPassword}
                  onChange={(e) => setNewPassword(e.target.value)}
                />
              </div>
              <div className="form-group">
                <label>ÏÉà ÎπÑÎ∞ÄÎ≤àÌò∏ ÌôïÏù∏</label>
                <input
                  type="password"
                  value={confirmNewPassword}
                  onChange={(e) => setConfirmNewPassword(e.target.value)}
                />
              </div>
              <div className="modal-buttons">
                <button type="submit">Ï†ÄÏû•</button>
                <button type="button" onClick={() => setIsEditModalOpen(false)}>Ï∑®ÏÜå</button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* ÌöåÏõêÌÉàÌá¥ Î™®Îã¨ */}
      {isDeleteModalOpen && (
        <div className="modal-overlay">
          <div className="modal">
            <h3>ÌöåÏõêÌÉàÌá¥</h3>
            <form onSubmit={handleDeleteAccount} className="delete-form">
              <div className="form-group">
                <label>ÎπÑÎ∞ÄÎ≤àÌò∏ ÌôïÏù∏</label>
                <input
                  type="password"
                  value={deletePassword}
                  onChange={(e) => setDeletePassword(e.target.value)}
                  placeholder="ÎπÑÎ∞ÄÎ≤àÌò∏Î•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
                  required
                />
              </div>
              <div className="modal-buttons">
                <button type="submit" className="delete-confirm-btn">ÌÉàÌá¥ÌïòÍ∏∞</button>
                <button 
                  type="button" 
                  onClick={() => setIsDeleteModalOpen(false)}
                >
                  Ï∑®ÏÜå
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      <div className="message-section">
        {!isOwner && (
          <form onSubmit={handleSubmit} className="message-form">
            <div className="sender-section">
              <div className="sender-type">
                <label>
                  <input
                    type="checkbox"
                    checked={isAnonymous}
                    onChange={(e) => setIsAnonymous(e.target.checked)}
                  />
                  ÏùµÎ™ÖÏúºÎ°ú Î≥¥ÎÇ¥Í∏∞
                </label>
              </div>
              {!isAnonymous && (
                <input
                  type="text"
                  value={senderName}
                  onChange={(e) => setSenderName(e.target.value)}
                  placeholder="Î≥¥ÎÇ¥Îäî ÏÇ¨Îûå Ïù¥Î¶Ñ"
                  required={!isAnonymous}
                  className="sender-input"
                />
              )}
            </div>
            <div className="message-input-container">
              <textarea
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                placeholder="Îî∞ÎúªÌïú ÌÅ¨Î¶¨Ïä§ÎßàÏä§ Î©îÏãúÏßÄÎ•º ÏûëÏÑ±Ìï¥Î≥¥ÏÑ∏Ïöî..."
                required
              />
              <div className="message-guidelines">
                <p className="warning">‚ö†Ô∏è ÎπÑÏÜçÏñ¥Í∞Ä Ìè¨Ìï®Îêú Î©îÏãúÏßÄÎäî Ï†ÑÏÜ°ÎêòÏßÄ ÏïäÏäµÎãàÎã§.</p>
                <div className="message-examples">
                  <p>üíå Î©îÏãúÏßÄ ÏûëÏÑ± ÏòàÏãú:</p>
                  <ul>
                    <li>"Î©îÎ¶¨ ÌÅ¨Î¶¨Ïä§ÎßàÏä§! Ïò¨Ìï¥ÎèÑ ÌñâÎ≥µÌïú ÌïòÎ£® Î≥¥ÎÇ¥ÏÑ∏Ïöî."</li>
                    <li>"Îî∞ÎúªÌïú Ïó∞Îßê Î≥¥ÎÇ¥ÏãúÍ≥† ÏÉàÌï¥ Î≥µ ÎßéÏù¥ Î∞õÏúºÏÑ∏Ïöî!"</li>
                    <li>"Ìï≠ÏÉÅ Í±¥Í∞ïÌïòÍ≥† ÌñâÎ≥µÌïòÏãúÍ∏∏ Î∞îÎûçÎãàÎã§."</li>
                  </ul>
                </div>
              </div>
            </div>
            <button type="submit">Î©îÏãúÏßÄ Î≥¥ÎÇ¥Í∏∞</button>
          </form>
        )}

        {isOwner && (
          <div className="messages-list">
            <h3>Î∞õÏùÄ Î©îÏãúÏßÄ Î™©Î°ù</h3>
            {messages.length > 0 ? (
              messages.map((msg, index) => (
                <div key={index} className="message-item">
                  <p>{msg.text}</p>
                  <div className="message-meta">
                    <small>From: {msg.sender}</small>
                    <small>{new Date(msg.createdAt).toLocaleDateString()}</small>
                  </div>
                </div>
              ))
            ) : (
              <div className="message-count-info">
                <p>üéÑ Î©îÏãúÏßÄÎäî ÌÅ¨Î¶¨Ïä§ÎßàÏä§ ÎãπÏùºÏóê ÌôïÏù∏Ìï† Ïàò ÏûàÏäµÎãàÎã§ üéÑ</p>
                <p>ÌòÑÏû¨ {messageCount}Í∞úÏùò Î©îÏãúÏßÄÍ∞Ä ÎèÑÏ∞©ÌñàÏäµÎãàÎã§!</p>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default UserPage;
