import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import './UserPage.css';
import axios from 'axios';

function UserPage() {
  const { username } = useParams();
  const [message, setMessage] = useState('');
  const [senderName, setSenderName] = useState('');
  const [isAnonymous, setIsAnonymous] = useState(false);
  const [messages, setMessages] = useState([]);
  const [messageCount, setMessageCount] = useState(0);
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('user'));
  const isOwner = user?.username === username;
  const [ownerDisplayName, setOwnerDisplayName] = useState('');

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await axios.get(`/api/users/${username}`);
        setOwnerDisplayName(response.data.displayName);
      } catch (error) {
        console.error('사용자 정보 조회 실패:', error);
      }
    };

    fetchUserData();
  }, [username]);

  useEffect(() => {
    const fetchMessages = async () => {
      try {
        const response = await axios.get(`/api/messages/${username}?isOwner=${isOwner}`);
        if (isOwner) {
          setMessages(response.data);
        } else {
          setMessageCount(response.data.count);
        }
      } catch (error) {
        console.error('메시지 조회 실패:', error);
      }
    };

    fetchMessages();
  }, [username, isOwner]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/messages', {
        text: message,
        recipientUsername: username,
        senderName: isAnonymous ? '익명' : senderName
      });

      // 메시지 전송 후 폼 초기화
      setMessage('');
      setSenderName('');
      setIsAnonymous(false);

      // 메시지 카운트 업데이트
      const response = await axios.get(`/api/messages/${username}?isOwner=false`);
      setMessageCount(response.data.count);

      alert('메시지가 전송되었습니다!');
    } catch (error) {
      console.error('메시지 저장 실패:', error);
      alert('메시지 저장에 실패했습니다.');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('user');
    navigate('/login');
  };

  const copyPageUrl = () => {
    const url = window.location.href;
    navigator.clipboard.writeText(url);
    alert('페이지 주소가 복사되었습니다!');
  };

  return (
    <div className="user-page">
      <header className="user-header">
        <h2>{ownerDisplayName}님의 크리스마스 메시지함</h2>
        {isOwner ? (
          <div className="owner-actions">
            <button onClick={copyPageUrl} className="share-btn">페이지 공유하기</button>
            <button onClick={handleLogout} className="logout-btn">로그아웃</button>
          </div>
        ) : (
          <div className="message-count">
            현재까지 {messageCount}개의 메시지가 도착했습니다
          </div>
        )}
      </header>

      <div className="message-section">
        {!isOwner && (
          <form onSubmit={handleSubmit} className="message-form">
            <div className="sender-section">
              <div className="sender-type">
                <label>
                  <input
                    type="checkbox"
                    checked={isAnonymous}
                    onChange={(e) => setIsAnonymous(e.target.checked)}
                  />
                  익명으로 보내기
                </label>
              </div>
              {!isAnonymous && (
                <input
                  type="text"
                  value={senderName}
                  onChange={(e) => setSenderName(e.target.value)}
                  placeholder="보내는 사람 이름"
                  required={!isAnonymous}
                  className="sender-input"
                />
              )}
            </div>
            <textarea
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              placeholder="따뜻한 크리스마스 메시지를 작성해보세요..."
              required
            />
            <button type="submit">메시지 보내기</button>
          </form>
        )}

        {isOwner && (
          <div className="messages-list">
            <h3>받은 메시지 목록</h3>
            {messages.map((msg, index) => (
              <div key={index} className="message-item">
                <p>{msg.text}</p>
                <div className="message-meta">
                  <small>From: {msg.sender}</small>
                  <small>{new Date(msg.createdAt).toLocaleDateString()}</small>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default UserPage;
