import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import './UserPage.css';
import axios from 'axios';

function UserPage() {
  const { username } = useParams();
  const [message, setMessage] = useState('');
  const [senderName, setSenderName] = useState('');
  const [isAnonymous, setIsAnonymous] = useState(false);
  const [messages, setMessages] = useState([]);
  const [messageCount, setMessageCount] = useState(0);
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('user'));
  const isOwner = user?.username === username;
  const [ownerDisplayName, setOwnerDisplayName] = useState('');
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);
  const [editDisplayName, setEditDisplayName] = useState('');
  const [currentPassword, setCurrentPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmNewPassword, setConfirmNewPassword] = useState('');
  const [deletePassword, setDeletePassword] = useState('');
  const [isDeleteModalOpen, setIsDeleteModalOpen] = useState(false);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await axios.get(`/api/users/${username}`);
        console.log('서버에서 받은 사용자 정보:', response.data);

        if (response.data.displayName) {
          setOwnerDisplayName(response.data.displayName);

          if (isOwner) {
            const userData = JSON.parse(localStorage.getItem('user'));
            const updatedUserData = {
              ...userData,
              displayName: response.data.displayName
            };
            localStorage.setItem('user', JSON.stringify(updatedUserData));
          }
        }
      } catch (error) {
        console.error('사용자 정보 조회 실패:', error);
      }
    };

    const userData = JSON.parse(localStorage.getItem('user'));
    if (userData?.displayName) {
      setOwnerDisplayName(userData.displayName);
    }

    fetchUserData();
  }, [username, isOwner]);

  useEffect(() => {
    const fetchMessages = async () => {
      try {
        const response = await axios.get(`/api/messages/${username}?isOwner=${isOwner}`);
        if (isOwner) {
          setMessages(response.data);
        } else {
          setMessageCount(response.data.count);
        }
      } catch (error) {
        console.error('메시지 조회 실패:', error);
      }
    };

    fetchMessages();
  }, [username, isOwner]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/messages', {
        text: message,
        recipientUsername: username,
        senderName: isAnonymous ? '익명' : senderName
      });

      // 메시지 전송 후 폼 초기화
      setMessage('');
      setSenderName('');
      setIsAnonymous(false);

      // 메시지 카운트 업데이트
      const response = await axios.get(`/api/messages/${username}?isOwner=false`);
      setMessageCount(response.data.count);

      alert('메시지가 전송되었습니다!');
    } catch (error) {
      console.error('메시지 저장 실패:', error);
      alert('메시지 저장에 실패했습니다.');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('user');
    navigate('/login');
  };

  const copyPageUrl = () => {
    const url = window.location.href;
    navigator.clipboard.writeText(url);
    alert('페이지 주소가 복사되었습니다!');
  };

  // 프로필 수정 모달 열기
  const openEditModal = () => {
    setEditDisplayName(ownerDisplayName);
    setCurrentPassword('');
    setNewPassword('');
    setConfirmNewPassword('');
    setIsEditModalOpen(true);
  };

  // 프로필 수정 처리
  const handleEditProfile = async (e) => {
    e.preventDefault();

    // 현재 비밀번호 필수 체크
    if (!currentPassword) {
      alert('현재 비밀번호를 입력해주세요.');
      return;
    }

    // 새 비밀번호 확인
    if (newPassword && newPassword !== confirmNewPassword) {
      alert('새 비밀번호가 일치하지 않습니다.');
      return;
    }

    try {
      const response = await axios.put(`/api/users/${username}`, {
        displayName: editDisplayName,
        currentPassword,
        newPassword: newPassword || undefined
      });

      // 상태 업데이트
      setOwnerDisplayName(response.data.displayName);

      // localStorage 업데이트
      const userData = JSON.parse(localStorage.getItem('user'));
      const updatedUserData = {
        ...userData,
        displayName: response.data.displayName
      };
      localStorage.setItem('user', JSON.stringify(updatedUserData));

      // 입력 필드 초기화
      setCurrentPassword('');
      setNewPassword('');
      setConfirmNewPassword('');

      setIsEditModalOpen(false);
      alert('프로필이 수정되었습니다.');
    } catch (error) {
      console.error('프로필 수정 실패:', error);
      alert(error.response?.data?.error || '프로필 수정에 실패했습니다.');
    }
  };

  // 회원탈퇴 처리
  const handleDeleteAccount = async (e) => {
    e.preventDefault();

    if (window.confirm('정말로 탈퇴하시겠습니까? 이 작업은 되돌릴 수 없습니다.')) {
      try {
        await axios.delete(`/api/users/${username}`, {
          data: { password: deletePassword }
        });

        localStorage.removeItem('user');
        alert('회원탈퇴가 완료되었습니다.');
        navigate('/');
      } catch (error) {
        console.error('회원탈퇴 실패:', error);
        alert(error.response?.data?.error || '회원탈퇴에 실패했습니다.');
      }
    }
  };

  return (
    <div className="user-page">
      <header className="user-header">
        <h2>{ownerDisplayName || '사용자'}님의 크리스마스 메시지함</h2>
        {isOwner ? (
          <div className="owner-actions">
            <button onClick={openEditModal} className="edit-btn">프로필 수정</button>
            <button onClick={copyPageUrl} className="share-btn">페이지 공유하기</button>
            <button onClick={handleLogout} className="logout-btn">로그아웃</button>
            <button 
              onClick={() => setIsDeleteModalOpen(true)} 
              className="delete-btn"
            >
              회원탈퇴
            </button>
          </div>
        ) : (
          <div className="message-count">
            현재까지 {messageCount}개의 메시지가 도착했습니다
          </div>
        )}
      </header>

      {/* 프로필 수정 모달 */}
      {isEditModalOpen && (
        <div className="modal-overlay">
          <div className="modal">
            <h3>프로필 수정</h3>
            <form onSubmit={handleEditProfile} className="edit-form">
              <div className="form-group">
                <label>표시 이름</label>
                <input
                  type="text"
                  value={editDisplayName}
                  onChange={(e) => setEditDisplayName(e.target.value)}
                  placeholder="표시될 이름을 입력하세요"
                  required
                />
              </div>
              <div className="form-group">
                <label>현재 비밀번호</label>
                <input
                  type="password"
                  value={currentPassword}
                  onChange={(e) => setCurrentPassword(e.target.value)}
                  placeholder="현재 비밀번호를 입력하세요"
                  required
                />
              </div>
              <div className="form-group">
                <label>새 비밀번호</label>
                <input
                  type="password"
                  value={newPassword}
                  onChange={(e) => setNewPassword(e.target.value)}
                />
              </div>
              <div className="form-group">
                <label>새 비밀번호 확인</label>
                <input
                  type="password"
                  value={confirmNewPassword}
                  onChange={(e) => setConfirmNewPassword(e.target.value)}
                />
              </div>
              <div className="modal-buttons">
                <button type="submit">저장</button>
                <button type="button" onClick={() => setIsEditModalOpen(false)}>취소</button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* 회원탈퇴 모달 */}
      {isDeleteModalOpen && (
        <div className="modal-overlay">
          <div className="modal">
            <h3>회원탈퇴</h3>
            <form onSubmit={handleDeleteAccount} className="delete-form">
              <div className="form-group">
                <label>비밀번호 확인</label>
                <input
                  type="password"
                  value={deletePassword}
                  onChange={(e) => setDeletePassword(e.target.value)}
                  placeholder="비밀번호를 입력하세요"
                  required
                />
              </div>
              <div className="modal-buttons">
                <button type="submit" className="delete-confirm-btn">탈퇴하기</button>
                <button 
                  type="button" 
                  onClick={() => setIsDeleteModalOpen(false)}
                >
                  취소
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      <div className="message-section">
        {!isOwner && (
          <form onSubmit={handleSubmit} className="message-form">
            <div className="sender-section">
              <div className="sender-type">
                <label>
                  <input
                    type="checkbox"
                    checked={isAnonymous}
                    onChange={(e) => setIsAnonymous(e.target.checked)}
                  />
                  익명으로 보내기
                </label>
              </div>
              {!isAnonymous && (
                <input
                  type="text"
                  value={senderName}
                  onChange={(e) => setSenderName(e.target.value)}
                  placeholder="보내는 사람 이름"
                  required={!isAnonymous}
                  className="sender-input"
                />
              )}
            </div>
            <textarea
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              placeholder="따뜻한 크리스마스 메시지를 작성해보세요..."
              required
            />
            <button type="submit">메시지 보내기</button>
          </form>
        )}

        {isOwner && (
          <div className="messages-list">
            <h3>받은 메시지 목록</h3>
            {messages.map((msg, index) => (
              <div key={index} className="message-item">
                <p>{msg.text}</p>
                <div className="message-meta">
                  <small>From: {msg.sender}</small>
                  <small>{new Date(msg.createdAt).toLocaleDateString()}</small>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default UserPage;
